export default class Currency {
  constructor(code, name) {
    this.name = name;
    this.code = code;
  }

  get name() {
    return this._name;
  }

  set name(n) {
    if (typeof n !== 'string') {
      throw new TypeError('Name must be a string');
    }
    this._name = n;
  }

  get code() {
    return this._code;
  }

  set code(c) {
    if (typeof c !== 'number') {
      throw new TypeError('Code must be a number');
    }
    this._code = c;
  }
  displayFullCurrency() {
    return `${this.name} (${this.code})`
  }
}
